mapper.select:
select t.is_import_time,t.id,t.user_id AS userId,t.content,t.create_time AS createTime,t.order_tel AS orderTel,d.name AS userName FROM t_operation_record t LEFT JOIN t_user d ON d.id=t.user_id
1=1
and t.order_tel IN
#{tel}
and t.is_import = #{isImport,jdbcType=VARCHAR}
order by t.create_time desc

mapper.insert:
insert into t_operation_record (id,user_id,user_name, content, order_id,create_time, order_tel,is_import,is_import_time)
values

    (#{item.id},#{item.userId},#{item.userName},#{item.content},#{item.orderId},#{item.createTime},#{item.orderTel},#{item.isImport},#{item.isImportTime}
    )
